import React, { useState } from 'react';
import { Alert, AlertDescription } from '@/components/ui/alert';

const F1Predictor = () => {
  const [error, setError] = useState('');
  const [formData, setFormData] = useState({
    gridPosition: 1,
    q1Time: 80.00,
    q2Time: 0.00,
    q3Time: 0.00,
    teamPoints: 0,
    teamAvgPoints: 0.00,
    driverPoints: 0,
    recentAvgPosition: 10.00,
    constructor: 0,
    track: 0,
    laps: 50,
    raceRound: 1,
    trackExperience: 0,
    avgTrackPosition: 10.00
  });

  const [predictions, setPredictions] = useState(null);
  const [showTrackHistory, setShowTrackHistory] = useState(false);

  const constructors = [
    "Mercedes", "Red Bull", "Ferrari", "McLaren", "Alpine",
    "AlphaTauri", "Aston Martin", "Williams", "Alfa Romeo", "Haas"
  ];

  const tracks = [
    "Bahrain", "Saudi Arabia", "Australia", "Azerbaijan", "Miami",
    "Monaco", "Spain", "Canada", "Austria", "Britain", "Hungary",
    "Belgium", "Netherlands", "Italy", "Singapore", "Japan",
    "Qatar", "USA", "Mexico", "Brazil", "Vegas", "Abu Dhabi"
  ];

  const handleChange = (name, value) => {
    // Input validation based on field type
    let validatedValue = value;
    switch (name) {
      case 'gridPosition':
        validatedValue = Math.max(1, Math.min(20, value));
        break;
      case 'q1Time':
      case 'q2Time':
      case 'q3Time':
        validatedValue = Math.max(0, Number(value.toFixed(2)));
        break;
      case 'teamPoints':
      case 'driverPoints':
      case 'laps':
      case 'raceRound':
      case 'trackExperience':
        validatedValue = Math.max(0, Math.floor(value));
        break;
      case 'teamAvgPoints':
      case 'recentAvgPosition':
      case 'avgTrackPosition':
        validatedValue = Math.max(0, Number(value.toFixed(2)));
        break;
      default:
        validatedValue = value;
    }

    setFormData(prev => ({
      ...prev,
      [name]: validatedValue
    }));
  };

  const handleSubmit = async () => {
    try {
      setError('');
      const response = await fetch('http://localhost:5000/api/predict', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          ...formData,
          BestQualiTime: Math.min(...[formData.q1Time, formData.q2Time, formData.q3Time].filter(t => t > 0)),
          PositionsGained: 0,
          year: new Date().getFullYear(),
          Constructor_encoded: formData.constructor,
          raceName_encoded: formData.track,
          Points: formData.driverPoints,
          TeamSeasonPoints: formData.teamPoints,
          TeamAvgPoints: formData.teamAvgPoints,
          RecentAvgPosition: formData.recentAvgPosition,
          TrackExperience: formData.trackExperience,
          AvgTrackPosition: formData.avgTrackPosition
        })
      });

      if (!response.ok) {
        throw new Error('Failed to fetch predictions');
      }

      const data = await response.json();
      setPredictions(data);
    } catch (error) {
      console.error('Error:', error);
      setError(error.message || 'Failed to get predictions');
    }
  };

  const renderValueControl = (name, value, step = 1, min = null, max = null) => {
    const decrementValue = () => {
      const newValue = value - step;
      if (min !== null && newValue < min) return;
      handleChange(name, newValue);
    };

    const incrementValue = () => {
      const newValue = value + step;
      if (max !== null && newValue > max) return;
      handleChange(name, newValue);
    };

    return (
      <div className="flex items-center bg-[#2a2a2a] rounded">
        <input
          type="number"
          value={typeof value === 'number' && step < 1 ? value.toFixed(2) : value}
          onChange={(e) => handleChange(name, Number(e.target.value))}
          className="w-full bg-transparent p-3 focus:outline-none"
          step={step}
          min={min}
          max={max}
        />
        <div className="flex">
          <button
            onClick={decrementValue}
            className="px-4 py-2 hover:bg-[#3a3a3a] transition-colors"
          >
            -
          </button>
          <button
            onClick={incrementValue}
            className="px-4 py-2 hover:bg-[#3a3a3a] transition-colors"
          >
            +
          </button>
        </div>
      </div>
    );
  };

  return (
    <div className="min-h-screen bg-[#1a1a1a] text-white p-8">
      <div className="max-w-7xl mx-auto">
        <div className="flex items-center gap-4 mb-6">
          <img src="/api/placeholder/48/48" alt="F1 Logo" className="h-12 w-12" />
          <h1 className="text-4xl font-bold">Formula 1 Race Prediction System</h1>
        </div>

        {error && (
          <Alert variant="destructive" className="mb-6">
            <AlertDescription>{error}</AlertDescription>
          </Alert>
        )}

        <p className="mb-8 text-gray-300">
          Enter race information to get predictions for race outcomes
        </p>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
          {/* Qualifying Performance */}
          <div>
            <h2 className="text-2xl font-bold mb-6">Qualifying Performance</h2>
            <div className="space-y-4">
              <div>
                <label className="block mb-2">Starting Grid Position</label>
                {renderValueControl('gridPosition', formData.gridPosition, 1, 1, 20)}
              </div>

              <div>
                <label className="block mb-2">Q1 Time (seconds)</label>
                {renderValueControl('q1Time', formData.q1Time, 0.1, 0)}
              </div>

              <div>
                <label className="block mb-2">Q2 Time (seconds)</label>
                {renderValueControl('q2Time', formData.q2Time, 0.1, 0)}
              </div>

              <div>
                <label className="block mb-2">Q3 Time (seconds)</label>
                {renderValueControl('q3Time', formData.q3Time, 0.1, 0)}
              </div>
            </div>
          </div>

          {/* Driver & Team Performance */}
          <div>
            <h2 className="text-2xl font-bold mb-6">Driver & Team Performance</h2>
            <div className="space-y-4">
              <div>
                <label className="block mb-2">Team's Championship Points</label>
                {renderValueControl('teamPoints', formData.teamPoints, 1, 0)}
              </div>

              <div>
                <label className="block mb-2">Team's Average Points per Race</label>
                {renderValueControl('teamAvgPoints', formData.teamAvgPoints, 0.1, 0)}
              </div>

              <div>
                <label className="block mb-2">Driver's Championship Points</label>
                {renderValueControl('driverPoints', formData.driverPoints, 1, 0)}
              </div>

              <div>
                <label className="block mb-2">Average Position (Last 3 Races)</label>
                {renderValueControl('recentAvgPosition', formData.recentAvgPosition, 0.1, 1, 20)}
              </div>
            </div>
          </div>

          {/* Race & Track Information */}
          <div>
            <h2 className="text-2xl font-bold mb-6">Race & Track Information</h2>
            <div className="space-y-4">
              <div>
                <label className="block mb-2">Constructor</label>
                <select
                  value={formData.constructor}
                  onChange={(e) => handleChange('constructor', Number(e.target.value))}
                  className="w-full bg-[#2a2a2a] p-3 rounded focus:outline-none"
                >
                  {constructors.map((team, index) => (
                    <option key={team} value={index}>{team}</option>
                  ))}
                </select>
              </div>

              <div>
                <label className="block mb-2">Track</label>
                <select
                  value={formData.track}
                  onChange={(e) => handleChange('track', Number(e.target.value))}
                  className="w-full bg-[#2a2a2a] p-3 rounded focus:outline-none"
                >
                  {tracks.map((track, index) => (
                    <option key={track} value={index}>{track}</option>
                  ))}
                </select>
              </div>

              <div>
                <label className="block mb-2">Track Experience (Races)</label>
                {renderValueControl('trackExperience', formData.trackExperience, 1, 0)}
              </div>

              <div>
                <label className="block mb-2">Average Track Position</label>
                {renderValueControl('avgTrackPosition', formData.avgTrackPosition, 0.1, 1, 20)}
              </div>

              <div>
                <label className="block mb-2">Number of Race Laps</label>
                {renderValueControl('laps', formData.laps, 1, 1)}
              </div>

              <div>
                <label className="block mb-2">Race Round Number</label>
                {renderValueControl('raceRound', formData.raceRound, 1, 1)}
              </div>
            </div>
          </div>
        </div>

        {/* Track History Section */}
        <div className="mt-8">
          <button
            onClick={() => setShowTrackHistory(!showTrackHistory)}
            className="w-full bg-[#2a2a2a] p-4 rounded text-left flex justify-between items-center transition-colors hover:bg-[#3a3a3a]"
          >
            <span>Track History Information</span>
            <span>{showTrackHistory ? '▼' : '▶'}</span>
          </button>

          {showTrackHistory && (
            <div className="mt-4 p-4 bg-[#2a2a2a] rounded">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <h3 className="text-xl font-semibold mb-4">Previous Results</h3>
                  <p className="text-gray-300">
                    Track experience: {formData.trackExperience} races
                  </p>
                  <p className="text-gray-300">
                    Average finish position: {formData.avgTrackPosition.toFixed(2)}
                  </p>
                </div>
              </div>
            </div>
          )}
        </div>

        {/* Make Predictions Button */}
        <button
          onClick={handleSubmit}
          className="mt-8 bg-[#e10600] text-white px-8 py-4 rounded-lg hover:bg-[#cc0500] transition-colors w-full md:w-auto"
        >
          Make Predictions
        </button>

        {/* Predictions Display */}
        {predictions && (
          <div className="mt-8 bg-[#2a2a2a] p-6 rounded-lg">
            <h2 className="text-2xl font-bold mb-6">Race Predictions</h2>
            <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-4 gap-6">
              {Object.entries(predictions).map(([outcome, probability]) => (
                <div
                  key={outcome}
                  className="bg-[#1a1a1a] p-4 rounded-lg"
                >
                  <h3 className="text-lg font-semibold mb-2">{outcome}</h3>
                  <div className="relative h-4 bg-[#3a3a3a] rounded overflow-hidden">
                    <div
                      className="absolute top-0 left-0 h-full bg-[#e10600] transition-all duration-500"
                      style={{ width: `${probability * 100}%` }}
                    />
                  </div>
                  <p className="text-2xl font-bold mt-2">{(probability * 100).toFixed(1)}%</p>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default F1Predictor;
